import React, { useState } from "react";
import style from "./Styles/Paginated.module.css"

export default function Paginated({ recipesPage, shownRecipes, paged, setPage, page }) {  // The object passed as parameter comes from the Home component

    const[input,setInput] = useState(1);  // Starting from page N°1

    const back = () => {
        setInput(parseInt(input) - 1);
        setPage(parseInt(input) - 1 )
    }
    const next = () => {
        setInput(parseInt(input) + 1);
        setPage(parseInt(input) + 1 )
    }

    let pag = (p) => {
        setInput(parseInt(p))
        paged(p);
    }

    const pages = [];

    for (let i = 1; i <= Math.ceil(shownRecipes / recipesPage); i++) {
        pages.push(i)
    };

    return (
        <div className={style.general}>
            <button className={style.bn} onClick={back} disabled={page <= 1}> ⮜ </button>
            {
                <nav>
                    <ul className={style.ul}>
                        {pages?.map((p) => (
                            <li className={style.list} key={p}>
                                <button className={style.btn} onClick={() => pag(p)}>{p}</button>
                            </li>
                        ))}
                    </ul>
                </nav>
            }
            <button className={style.bn} onClick={next} disabled={page === pages.length}> ⮞ </button>
        </div>
    )
}
